PREFIX agents: <http://www.hsu-hh.de/aut/ontologies/agent-summer-school#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX DINEN61360: <http://www.hsu-ifa.de/ontologies/DINEN61360#>
DELETE {
    ?agent rdf:type agents:GripperAgent;
		agents:hasCapability ?cap.
    ?cap agents:hasInput ?capInput;
         agents:hasOutput ?capOutput.
    ?capInput rdf:type ?inputType.
    ?capOutput rdf:type ?outputType.
    ?capInput DINEN61360:has_Data_Element ?inputDataElement.
    ?inputDataElement a DINEN61360:Data_Element;
                      DINEN61360:has_Instance_Description ?inputInstanceDesc.
    ?inputInstanceDesc a DINEN61360:Instance_Description;
                       DINEN61360:Expression_Goal ?inEx;
                       DINEN61360:Logic_Interpretation ?inLogic;
                       DINEN61360:Value ?inValue.
    ?capOutput DINEN61360:has_Data_Element ?outputDataElement;
               DINEN61360:has_Instance_Description ?outputInstanceDesc.
     ?outputInstanceDesc a DINEN61360:Instance_Description;
                       DINEN61360:Expression_Goal ?outEx;
                       DINEN61360:Logic_Interpretation ?outLogic;
                       DINEN61360:Value ?outValue.
    ?outputDataElement a DINEN61360:Data_Element.
} 
WHERE {
    BIND(agents:Gripper_A AS ?agent)
    OPTIONAL {
        ?agent agents:hasCapability ?cap.
    }
    OPTIONAL {
        ?cap agents:hasInput ?capInput;
         agents:hasOutput ?capOutput.
        ?capInput rdf:type ?inputType.
    	?capOutput rdf:type ?outputType.
    }
    OPTIONAL {
         ?capInput DINEN61360:has_Data_Element ?inputDataElement.
        ?inputDataElement a DINEN61360:Data_Element;
                          DINEN61360:has_Instance_Description ?inputInstanceDesc.
        ?inputInstanceDesc a DINEN61360:Instance_Description;
                           DINEN61360:Expression_Goal ?inEx;
                           DINEN61360:Logic_Interpretation ?inLogic;
                           DINEN61360:Value ?inValue.
    }
    OPTIONAL {
    	?capOutput DINEN61360:has_Data_Element ?outputDataElement;
               DINEN61360:has_Instance_Description ?outputInstanceDesc.
         ?outputInstanceDesc a DINEN61360:Instance_Description;
                           DINEN61360:Expression_Goal ?outEx;
                           DINEN61360:Logic_Interpretation ?outLogic;
                           DINEN61360:Value ?outValue.
        ?outputDataElement a DINEN61360:Data_Element.
    }
}`;
return msg;

